%% drift_vector_field.m
% Inputs:
%   x: the state vector, x = [x_top;y_top;theta_1;theta_2;theta_3;dx_top;dy_top;dtheta_1;dtheta_2;dtheta_3];
%   params: a struct with many elements, generated by calling init_params.m
%
% Outputs:
%   f_ss: a 10x1 vector that maps state x to dx (time derivative of
%       the state x) when control input u = 0.
function f_ss = drift_vector_field(x,params)
    theta_1 = x(3);
    theta_2 = x(4);
    theta_3 = x(5);
    dx_top = x(6);
    dy_top = x(7);
    dtheta_1 = x(8);
    dtheta_2 = x(9);
    dtheta_3 = x(10);
    f_ss = autogen_drift_vector_field(params.model.dyn.bot.I,...
                                      params.model.dyn.mid.I,...
                                      params.model.dyn.top.I,...
                                      params.model.dyn.b1,...
                                      params.model.dyn.b2,...
                                      params.model.dyn.b3,...
                                      params.model.dyn.b4,...
                                      params.model.dyn.b5,...
                                      dtheta_1,...
                                      dtheta_2,...
                                      dtheta_3,...
                                      dx_top,...
                                      dy_top,...
                                      params.model.dyn.g,...
                                      params.model.dyn.mid.l,...
                                      params.model.dyn.top.l,...
                                      params.model.dyn.bot.m,...
                                      params.model.dyn.mid.m,...
                                      params.model.dyn.motor1.m,...
                                      params.model.dyn.motor2.m,...
                                      params.model.dyn.top.m,...
                                      params.model.dyn.mid.r_com,...
                                      params.model.dyn.bot.r_com,...
                                      params.model.dyn.top.r_com,...
                                      theta_1,...
                                      theta_2,...
                                      theta_3);









end